<template>
  <div
    class="cus-body"
    :style="setHeight != true ? 'height:' + bgHeight + 'px' : ''"
    style="color: #fff"
  >
    <div class="cus-header">
      <span class="cus-title"
        ><span>{{ option.title }}</span></span
      >
    </div>
    <div class="piebody">
      <el-col :span="12" class="pie-one-dom">
        <div class="pie-one" :id="`pie-one${option.domId}`"></div>
        <h3 class="last-days">
          剩余天数<span class="days">{{ lastDays }} 天</span>
        </h3>
      </el-col>

      <el-col :span="12" class="base-info">
        <h3>基本信息</h3>
        <div class="base-info_1">
          <ul
            :style="`font-size:${
              info != true ? infoSize : ''
            }px;line-height:${infoLineHeight}px;`"
            class="cc rowup"
          >
            <li><span>开工时间：</span>{{ startTime }}</li>
            <li><span>建筑结构类型：</span>{{ buildStructureType }}</li>
            <li><span>单体数量(个)：</span>{{ numMonomers }}</li>
            <li><span>建筑面积(m)：</span>{{ buildArea }}</li>
            <li><span>地上/地下面积：</span>{{ groundUndergroundArea }}</li>
            <li><span>建筑高度(m)：</span>{{ buildHeight }}</li>
            <li><span>基坑深度(m)：</span>{{ foundationPitDepth }}</li>
            <li><span>建设单位：</span>{{ constructionUnit }}</li>
            <li><span>设计单位：</span>{{ designer }}</li>
            <li><span>监理单位：</span>{{ controlUnit }}</li>
            <li><span>施工单位：</span>{{ ceneralContractUnit }}</li>
            <li><span>质量目标：</span>{{ qualityObjectives }}</li>
            <li><span>安全目标：</span>{{ safetyObjectives }}</li>
          </ul>
        </div>
      </el-col>
    </div>
  </div>
</template>
<script>
import axios from "axios";
import { getUrlParames } from "@/api/httpClient.js";
export default {
  name: "pieone",
  props: {
    option: Object,
    component: Object,
  },
  data() {
    return {
      chartDom: Date.parse(new Date()),
      lastDays: null,
      startTime: null,
      buildStructureType: null,
      numMonomers: null,
      buildArea: null,
      groundUndergroundArea: null,
      buildHeight: null,
      foundationPitDepth: null,
      constructionUnit: null,
      designer: null,
      controlUnit: null,
      ceneralContractUnit: null,
      qualityObjectives: null,
      safetyObjectives: null,
      params: {
        projectId: null,
        companyId: null,
      },
    };
  },
  created() {
    if (this.option.domId === null) {
      this.option.domId = this.chartDom;
    }
  },
  computed: {
    fontSize() {
      return (this.option.fontSize || 30) + "px";
    },
    chartData() {
      return this.option.data;
    },
    bgHeight() {
      return this.option.backgroundHeight;
    },
    setHeight() {
      return this.option.setHeight;
    },
    info() {
      return this.option.info;
    },
    infoSize() {
      return this.option.infoSize;
    },
    infoLineHeight() {
      return this.option.infoLineHeight;
    },
    infoPaddingTop() {
      return this.option.infoPaddingTop;
    },
  },
  mounted() {
    let that = this;
    setTimeout(function () {
      that.getCharts();
    }, 1000);
    if (getUrlParames("userType") == 1) {
      //项目级
      this.params.projectId = getUrlParames("userId");
    } else {
      //企业级
      this.params.companyId = getUrlParames("userId");
    }
    axios.defaults.headers.common["Authorization"] = getUrlParames("token");
  },

  methods: {
    getCharts() {
      let url = `/prod-api/wisdomScreen/home/wsHome/projectOverview`;
      axios
        .get(url, {
          params: this.params,
        })
        .then((res) => {
          if (res.data.code === 200) {
            let row = res.data.data;
            this.lastDays = row.daysRemaining;
            this.startTime = row.startTime;
            this.buildStructureType = row.buildStructureType;
            this.numMonomers = row.numMonomers;
            this.buildArea = row.buildArea;
            this.groundUndergroundArea = row.groundUndergroundArea;
            this.buildHeight = row.buildHeight;
            this.foundationPitDepth = row.foundationPitDepth;
            this.constructionUnit = row.constructionUnit;
            this.designer = row.designer;
            this.controlUnit = row.controlUnit;
            this.ceneralContractUnit = row.ceneralContractUnit;
            this.qualityObjectives = row.qualityObjectives;
            this.safetyObjectives = row.safetyObjectives;
            if(row.projectProgress == null || row.projectProgress == ''){
              this.addPie(0);
            }else{
              this.addPie(row.projectProgress);
            }
          }
        });
    },
    addPie(projectProgress) {
      let pieOne = echarts.init(
        document.getElementById(`pie-one${this.option.domId}`)
      );
      var data = projectProgress; //数值大小
      var max = 100; //满刻度大小
      var option = {
        title: {
          text: "工程进度",
          top: "60%",
          left: "center",
          textStyle: {
            color: "#fff",
            fontSize: 18,
          },
        },
        color: ["rgba(255,255,255,.1)"],
        series: [
          {
            type: "pie",
            center: ["50%", "55%"],
            radius: ["75%", "87%"],
            hoverAnimation: false,
            itemStyle: {
              normal: {
                labelLine: {
                  show: false,
                },
              },
            },
            data: [
              {
                name: "",
                value: data,
                itemStyle: {
                  normal: {
                    color: {
                      // 完成的圆环的颜色
                      colorStops: [
                        {
                          offset: 0,
                          color: "rgba(0,144,255,1)", // 0% 处的颜色
                        },
                        {
                          offset: 1,
                          color: "#02d6fc", // 100% 处的颜色
                        },
                      ],
                    },
                  },
                },
                label: {
                  show: true,
                  position: "center",
                  color: "#00fcf9",
                  fontSize: 28,
                  fontWeight: "bold",
                  formatter: function (o) {
                    return data + "%";
                  },
                },
              },
              {
                //画中间的图标
                name: "",
                value: 0,
                label: {
                  position: "inside",
                  backgroundColor: {
                    image:
                      "data:image/png;base64,",
                  },
                  width: 10,
                  height: 10,
                  borderRadius: 10,
                  padding: 10,
                },
              },
              {
                //画剩余的刻度圆环
                name: "",
                value: max - data,
                label: {
                  show: false,
                },
              },
            ],
          },
        ],
      };

      pieOne.setOption(option);
      let chartDom = document.getElementsByTagName("avue-draggable");
      console.log(chartDom);
      chartDom.onresize = function () {
        pieOne.resize();
      };
    },
  },
  watch: {},
};
</script>
